mapper.select:
select b.*
from (
	select (ROW_NUMBER() OVER()) AS ROWNUM,a.*
	from (
		select 
				pop_up_id,
			   title,
			   COALESCE(update_at,create_at) as create_At,
			   pop_start,
			   pop_end 
		from tb_popup_main
		where 1=1
		order by create_At desc 
	) a 
) b
where rownum between  #{startNum} and #{endNum}

mapper.insert:
insert into tb_popup_main (	create_at
							,pop_start
							,pop_end
							,title
							,content
							,writer_id )
values (NOW()
		,#{popStart}
		,#{popEnd}
		,#{title}
		,#{content}
		,#{writerId}
)
returning pop_up_id
